import React, { Component } from 'react';
import './Me.css';

import _user from '../user.png';


const {create} = require('ipfs-http-client');

const ipfs = create({host: 'ipfs.infura.io', port:5001, protocol:'https'});

class Me extends Component {

	constructor(props) {

    super(props);
   
    this.state = {


    };

  }


  /*


	struct Person {
        
		bool approved_as_artist; //// Address is a Diskify approved artist. (Sic.)
		
		uint256 my_albums_counter; //// (Sic.)
		
		uint256[] owned_albums; //// (Sic.)
		
		uint256 created_on; //// Account creation date. (Sic.)
		
	}
	


  */


	render(){

		return (

			<div>
				<div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex grid grid-cols-1 text-center">
              <div className="mr-auto ml-auto -mt-8 w-1/2">
                
                <div className=' self-center ml-auto mr-auto w-1/2 h-1/2 min-h-96 min-w-96 max-w-96 max-h-96 border-4 border-black'> 
                <img src={_user} id='img-cover' className="h-full w-full Album_cover_display" alt="album cover image" />
                </div>
              
                <p className='h-auto mt-4 text-4xl font-bold font-mono'> My Info </p>
                
                <form onSubmit={this.onSubmit}> 
                  
                  <input type='text' id='input-artistname' className='font-mono w-2/3 bg-gray-100 border-gray-300 h-auto rounded-lg text-3xl p-4 mb-2 hover:bg-gray-100 border-4 border-transparent hover:border-black' value=" Creation Date: 2021-10-20" disabled/>
                  <br/>

                  <input type='text' id='input-albumname' className='font-mono w-2/3 bg-gray-100 border-gray-300 h-auto rounded-lg text-3xl p-4 mb-2 hover:bg-gray-100 border-4 border-transparent hover:border-black' placeholder=" My Name " />
                  <br/>
                  
                  <textarea id='textarea-displaytracks' className='bg-gray-100 border-4 border-gray-300 rounded-sm mt-2 w-2/3 h-40 font-mono text-xl bg-sroll'> My bio :) </textarea>

                  <input type='text' id='input-albumgenres' className='font-mono w-2/3 bg-gray-100 border-gray-300 h-auto rounded-lg text-3xl p-4 hover:bg-gray-100 border-4 border-transparent hover:border-black' placeholder=" Album Genres " />
                  <br/>

                  <br/>
                  <label for='input-albumcover' className="w-5/6 h-auto text-4xl font-bold font-mono"> Select Image for Album Cover: </label><br/>
                  <input type='file' id='input-albumcover' className='w-2/3 h-auto text-3xl' onChange={this.captureAlbumCover} />
                  <br/>

                  <br/>
                  <label for='input-tracks' className="w-5/6 h-auto text-4xl font-bold font-mono"> Select Image for Album Cover: </label><br/>
                  <input type='file' id='input-tracks' className="w-2/3 h-auto text-3xl" onChange={this.captureFiles} webkitdirectory mozdirectory msdirectory odirectory directory multiple="multiple" />
                  <br/>


                  <br/>
                  <input type='submit' className='w-2/3 mt-2 mb-2 text-4xl h-auto font-bold font-mono text-gray-100 hover:text-black bg-black border-4 border-black hover:bg-white' value="Save Changes" />
                  <br/>
                  <br/>

                </form>

              </div>
            </main>
          </div>
        </div>

			</div>

		);

	}

}


export default Me;
