import React, { Component } from 'react';
import logo from '../logo.png';
import './App.css';


const {create} = require('ipfs-http-client');

const ipfs = create({host: 'ipfs.infura.io', port:5001, protocol:'https'});


class App extends Component {

  constructor(props) {

    super(props);
    this.state = {

      buffer: [],
      hash: null

    };

  }

  captureFile = (event) => {

    event.preventDefault();

    for(var i = 0; i < event.target.files.length; i++){
      
      const file = event.target.files[0];
      const reader = new window.FileReader();
      reader.readAsArrayBuffer(file);


      reader.onloadend = () => {
        this.state.buffer.push(Buffer(reader.result));        
      }
  
      var s = event.target.files[i].name + '\n';
      s += event.target.files[i].size + ' Bytes\n';
      s += event.target.files[i].type;
      console.log(s);

    }
    
    console.log("fincapture");


    console.log(this.state);


  }

  onSubmit = (event) => {

    event.preventDefault();
    console.log("Submitting the form...");

    const _testing = {"buffer":  ["laik", "ads"], "a":'object', "s":5};
    
    const result = ipfs.add(JSON.stringify(_testing));

    const _this = this;

    var _hash = "";
    result.then(function(out){

      _hash = out['path'];

      _this.setState({ hash: _hash });

    });

    console.log("3rd  ","https://ipfs.infura.io/ipfs/"+this.state.hash);

    console.log("fin submit");

  }


  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Meme of the day
          </a>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={logo} className="App-logo" alt="logo" />
                </a>
                <p> &nbsp; </p>

                <h1>Change meme</h1>
                <form onSubmit={this.onSubmit}> 
                  <input type='file' onChange={this.captureFile} webkitdirectory mozdirectory msdirectory odirectory directory multiple="multiple" />
                  <input type='submit' />
                </form>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
