import React, { Component } from 'react';
import './Diskify.css';

import Web3 from 'web3';
import Abi_Diskify from '../abis/Diskify.json';

const {create} = require('ipfs-http-client');

const ipfs = create({host: 'ipfs.infura.io', port:5001, protocol:'https'});


class Diskify extends Component {

	async componentWillMount() {

    await this.loadWeb3();

    //await this.loadBlockchainData();

    await this.teste();

  }

  async teste(){

  	const web3 = window.web3;

    const accounts = await web3.eth.getAccounts();

    this.eth_account = accounts[0];

    console.log(accounts);

    const abi = Abi_Diskify.abi;
      
    this.contract = new web3.eth.Contract(abi, "0xe0b0Ef4879202286503734484184BEEF3675DCfe"); // fetch contract
    
    console.log(this.contract);


    this.contract.methods.getAlbumIdCounter().call().this((res) => {

    	console.log(res);

    })


  }


	constructor (props) {

		super(props);

		this.state = {};

		this.contract = '';

		this.eth_account = '';


	}


  async loadWeb3() {

	  if (window.ethereum) {

	    window.web3 = new Web3(window.ethereum);
	    await window.ethereum.enable();

	  } if (window.web3) {

	    window.web3 = new Web3(window.web3.currentProvider);

	  } else {

	  	console.log("User did not connect its metamask wallet.");

	  }

	}


	render(){

		return (

			<div>

			</div>

		);

	}

}


export default Diskify;
