import React, { Component } from 'react';
import logo from '../logo.png';
import './App.css';

import NavBar from './NavBar';



const {create} = require('ipfs-http-client');

const ipfs = create({host: 'ipfs.infura.io', port:5001, protocol:'https'});


class App extends Component {

  constructor(props) {

    super(props);
    this.state = {

      artist_address: null,
      
      artist_name: null,

      release_date: null,
      
      album_name: null, 

      genres: null,
          
      track_names: [],

      album_cover: null, 

      track_hashes: [], 
      
      hash: null

    };

  }





  captureAlbumCover = (event) => {

      
  }



  captureFiles = (event) => {

    var tracks = [];

    event.preventDefault();

    for(var i = 0; i < event.target.files.length; i++){
      
      const file = event.target.files[i];
      const reader = new window.FileReader();
      reader.readAsArrayBuffer(file);

      reader.onloadend = () => {

        tracks.push(Buffer(reader.result));        
        
        this.state.track_names.push(event.target.files[i].name);

      }
  
    }
    

    for(var i = 0; i < tracks.length; i++){

      ipfs.add(tracks[i]).then(function(out){

        this.state.track_hashes.push( 'https://ipfs.infura.io/ipfs/' + out['path'] );

      });

    }

    console.log("fincapture");

    console.log(this.state);


  }

  onSubmit = (event) => {

    event.preventDefault();


    console.log("Submitting the form...");


    this.state.album_name = document.getElementById("input-albumname").value;

    this.state.artist_name = document.getElementById("input-artistname").value;

    this.state.genres = document.getElementById("input-albumname").value;


    /*

        this.state = {

      artist_address: null,
    
      release_date: null,
          

      album_cover: null, 

      hash: null

    };



    */




    const _testing = {"buffer":  ["laik", "ads"], "a":'object', "s":5};
    
    const result = ipfs.add(JSON.stringify(_testing));

    const _this = this;

    var _hash = "";
    result.then(function(out){

      _hash = out['path'];

      _this.setState({ hash: _hash });

    });

    console.log("3rd  ","https://ipfs.infura.io/ipfs/"+this.state.hash);

    console.log("fin submit");

  }


  render() {
    return (
      <div>

        <NavBar></NavBar>

        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={logo} className="App-logo" alt="logo" />
                </a>
                <p> &nbsp; </p>

                <h1> Mint Your Album </h1>
                
                <form onSubmit={this.onSubmit}> 
                  
                  <input type='text' id='input-albumname' placeholder=" Album's Name " />
                  <br/>

                  <input type='text' id='input-artistname' placeholder=" Artist Name(s) " />
                  <br/>

                  <input type='text' id='input-albumgenres' placeholder=" Album Genres " />
                  <br/>

                  <br/>
                  <label for='input-albumcover'> Select Image for Album Cover: </label><br/>
                  <input type='file' id='input-albumcover' />
                  <br/>

                  <br/>
                  <label for='input-tracks'> Select Image for Album Cover: </label><br/>
                  <input type='file' id='input-tracks' onChange={this.captureFiles} webkitdirectory mozdirectory msdirectory odirectory directory multiple="multiple" />
                  <br/>

                  <input type='submit' value="Mint" />
                
                </form>

              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
