import React, { Component } from 'react';
import './Search.css';


import Web3 from 'web3';
import Diskify from '../abis/Diskify.json';

const {create} = require('ipfs-http-client');

const ipfs = create({host: 'ipfs.infura.io', port:5001, protocol:'https'});


class Search extends Component {

	async componentWillMount() {

    await this.loadWeb3();

    //await this.loadBlockchainData();

  }


  constructor (props) {

  	super(props);

  	this.state = {};

  }


  async loadWeb3() {

	  if (window.ethereum) {

	    window.web3 = new Web3(window.ethereum);
	    await window.ethereum.enable();

	  } if (window.web3) {

	    window.web3 = new Web3(window.web3.currentProvider);

	  } else {

	  	console.log("User did not connect its metamask wallet.");

	  }

	}


	render(){

		return (

			<div>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex grid grid-cols-1 text-center">
              <div className="mr-auto ml-auto -mt-8 w-5/6">
              

                <input type='text' id='input-search' className='font-mono w-5/6 bg-gray-100 border-gray-300 h-auto rounded-lg text-3xl p-4 mb-2 hover:bg-gray-100 border-4 border-transparent hover:border-black' placeholder=" Search... " />
                <br/>

                
                
              </div>
            </main>
          </div>
        </div>
      </div>




		);

	}

}


export default Search;
