import React, { Component } from 'react';
import './Diskify.css';

import Web3 from 'web3';
import Abi_Diskify from '../abis/Diskify.json';

import logo_ from '../logo.png'; 


const {create} = require('ipfs-http-client');

const ipfs = create({host: 'ipfs.infura.io', port:5001, protocol:'https'});


class Diskify extends Component {

	async componentWillMount() {

    await this.loadWeb3();

    //await this.loadBlockchainData();

    await this.teste();


    var _img = document.getElementById('albumCoverPhoto');

    _img.onload = () => { URL.revokeObjectURL(_img.src); };

    _img.src = logo_;


  }

  async teste(){

  	const web3 = window.web3;

    const accounts = await web3.eth.getAccounts();

    this.eth_account = accounts[0];

	  const networdId = await web3.eth.net.getId();
    const networkData = Abi_Diskify.networks[networdId];

    console.log(networkData, networdId)

    if (networkData) {

      const abi = Abi_Diskify.abi;
      this.contract = new web3.eth.Contract(abi, networkData.address);


    } else { window.alert("Contract not deployed."); return; }
  

    const _this = this;

    _this.contract.methods.album_id_counter()
    .call({gasPrice:20 , gas: 200000000})
    .then( (res) => {

    	console.log(res);

    })
    .catch( _ => {

    	console.log('nnn');

    });


  }


	constructor (props) {

		super(props);

		this.state = {};

		this.contract = '';

		this.eth_account = '';


	}


  async loadWeb3() {

	  if (window.ethereum) {

	    window.web3 = new Web3(window.ethereum);
	    await window.ethereum.enable();

	  } if (window.web3) {

	    window.web3 = new Web3(window.web3.currentProvider);

	  } else {

	  	console.log("User did not connect its metamask wallet.");

	  }

	}


	render(){

		return (

			<div>


				<p className="text-4xl font-bold italic ml-20 mb-10 text-gray-800"> On Highlight: </p>

				<div className="grid grid-flow-col grid-rows-6 grid-cols-2 gap-1 lg:flex lg:items-center mt-8 mb-20 h-96 p-10 sm:p-8 object-center">
				
					<div className="h-96 object-scale-down row-span-6 object-contain">
						
						<img className="object-scale-down w-1/2 m-auto max-h-full" id="albumCoverPhoto" /> 
					
					</div>

			<div className="w-1/2 m-auto object-left">
					
				<p className="text-3xl -mt-8 italic font-bold" id="albumName"> On My Way To Infinity </p> 
				<p className="text-lg mt-1 italic text-gray-400"> Rock, Alternative, Blues </p> 

				<div className="grid grid-flow-col grid-rows-1 grid-cols-2 gap-1 lg:flex lg:items-center mt-2 p-2 sm:p-1 object-left">
					
					<img className="w-8 h-8 rounded-full" src="/onauction/girl.jpg" id="bandCoverPhoto" />

					<p className="text-xl ml-2 italic" id="bandName"> A Fictitious Band Name </p> 

				</div>

				<p className="mt-4 font-bold text-xl object-center"> Released at 2020 June 11, 12:48 </p> 

				<div className="grid grid-flow-col grid-rows-1 grid-cols-1 gap-1 lg:flex lg:items-center object-center">
					
					<div className="grid grid-flow-col grid-rows-3 grid-cols-1 gap-1 p-10 sm:p-8 object-center justify-self-center">
						
						<p className="text-lg italic"> Current Bid </p> 
						<p className="text-xl font-bold"> 1.5 ETH </p>  
						<p className="text-xl text-gray-400"> $2500 </p> 

					</div>
				
				</div>

				<button onclick="window.location.href='/view?aid=beeple';" className="justify-center h-20 w-40 rounded-lg bg-black text-white text-3xl border-4 border-black hover:bg-gray-100 hover:text-black"> View </button>

			</div>

		</div>


			</div>

		);

	}

}


export default Diskify;
