import React, { Component } from 'react';
import './Mint.css';

import _cover_placeholder from '../cover_placeholder.png';


// Get artist address from metamask.

const {create} = require('ipfs-http-client');

const ipfs = create({host: 'ipfs.infura.io', port:5001, protocol:'https'});


class Mint extends Component {

  constructor(props) {

    super(props);
   
    this.state = {

      artist_address: null,
      
      artist_name: null,

      release_date: null,
      
      album_name: null, 

      genres: null,
          
      track_names: [],

      album_cover: null, 

      track_hashes: [], 
      
      album_hash: null

    };

    this.album_cover_pushed = false;

    this.files_pushed = 0;
    this.files_counter = 0;

  }


  captureAlbumCover = (event) => {

    const file = event.target.files[0];
    const reader = new window.FileReader();
    reader.readAsArrayBuffer(file);

    const _this = this;
    this.album_cover_pushed = false;

    reader.onloadend = () => {

      const _cover = Buffer(reader.result);

      ipfs.add(_cover).then(function(out){

        console.log("Album cover hash: ", out['path']);

        _this.state.album_cover = 'https://ipfs.infura.io/ifps/' + out['path'];

        _this.album_cover_pushed = true;

      });

    }

    console.log("Album cover captured.")

  }


  captureFiles = (event) => {

    event.preventDefault();

    const _this = this;

    this.files_pushed = event.target.files.length;
    this.files_counter = 0;

    for(var i = 0; i < event.target.files.length; i++){
      
      const file = event.target.files[i];
      
      _this.state.track_names.push(file.name);
      
      const reader = new window.FileReader();
      
      reader.readAsArrayBuffer(file);

      reader.onloadend = () => {

        console.log("Track (", file.name,") captured.");
 
        const bdata = Buffer(reader.result);

        ipfs.add(bdata).then( function(out) {

          console.log("Track (",file.name,") hash: ", out['path']);

          _this.state.track_hashes.push( 'https://ipfs.infura.io/ipfs/' + out['path'] );

          _this.files_counter += 1;

        });        

      }
  
    }

    console.log("Tracks captured.");

  }


  onSubmit = (event) => {

    event.preventDefault();

    console.log("Submitting the form...");

    while( this.album_cover_pushed === false ){ return; }

    while( this.files_counter < this.files_pushed ){ return; } 

    this.state.album_name = document.getElementById("input-albumname").value;

    this.state.artist_name = document.getElementById("input-artistname").value;

    this.state.genres = document.getElementById("input-albumname").value;


    const currentDate = new Date();

    this.state.release_date = 
      currentDate.getFullYear().toString()+"-"+
      (currentDate.getMonth()+1).toString()+"-"+
      currentDate.getDate();

    this.state.artist_address = "0x GET ADDRESS OF SENDER!!";


    const _this = this;
    var _hash = "";

    ipfs.add( JSON.stringify(this.state) ).then( function(out) { 

      console.log("Minted Album Hash: ", out['path']);

      _this.state.album_hash = out['path'];

      console.log( 'https://ipfs.infura.io/ipfs/' + out['path'] );

    });

    console.log("Mint done.");

  }


  render() {
    return (
      <div>

        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className=" mr-auto ml-auto -mt-2 bg-red-200 w-1/2 h-screen">
                
                <img src={_cover_placeholder} className="self-center ml-auto mr-auto w-1/2 h-1/2 Album_cover_display" alt="album cover image" />
                <p> &nbsp; </p>

                <h1> Mint Your Album </h1>
                
                <form onSubmit={this.onSubmit}> 
                  
                  <input type='text' id='input-albumname' placeholder=" Album's Name " />
                  <br/>

                  <input type='text' id='input-artistname' placeholder=" Artist Name(s) " />
                  <br/>

                  <input type='text' id='input-albumgenres' placeholder=" Album Genres " />
                  <br/>

                  <br/>
                  <label for='input-albumcover'> Select Image for Album Cover: </label><br/>
                  <input type='file' id='input-albumcover' onChange={this.captureAlbumCover} />
                  <br/>

                  <br/>
                  <label for='input-tracks'> Select Image for Album Cover: </label><br/>
                  <input type='file' id='input-tracks' onChange={this.captureFiles} webkitdirectory mozdirectory msdirectory odirectory directory multiple="multiple" />
                  <br/>

                  <input type='submit' value="Mint" />
                
                </form>

              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default Mint;
